mapper.select:
SELECT a.*, b.user_name, nvl(c.jjoa_count,0) jjoa_count FROM bookreport a INNER JOIN customer_info b ON a.user_email = b.user_email LEFT OUTER JOIN (SELECT count(*) jjoa_count, bookreport_no FROM jjoa group by bookreport_no) c ON a.bookreport_no = c.bookreport_no
WHERE a.report_kind = #{report_kind}
ORDER BY a.bookreport_no desc

mapper.update:
UPDATE bookreport
SET view_count = view_count+1
WHERE bookreport_no = ${bookreport_no}
